  var duePosition = paidToDatePosition + 3;
  doc.font("Helvetica-Bold");
  generateTableRow(doc, duePosition, "", "Total", formatCurrency(invoice.total));
  doc.font("Helvetica");
  finalLine = duePosition + 10;
}

function generateFooter(doc) {
